import React, { useRef, useState } from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function Advantages() {
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 2,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                    infinite: true,
                    dots: false
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                    initialSlide: 1
                }
            }
        ]
    };

    const sliderRef = useRef(null);
    const [dragStartX, setDragStartX] = useState(null);

    const handleMouseDown = (event) => {
        setDragStartX(event.clientX);
    };

    const handleMouseMove = (event) => {
        if (dragStartX !== null) {
            const slider = sliderRef.current;
            const deltaX = event.clientX - dragStartX;
            if (slider) {
                slider.scrollLeft -= deltaX;
            }
            setDragStartX(event.clientX);
        }
    };

    const handleMouseUp = () => {
        setDragStartX(null);
    };

    return (
        <div className="advantages container">
            <h2 className="advantages-title section-title flex">
                <span>
                    Почему партнеры
                </span>
                <span style={{ display: 'block' }}>
                    <i style={{color: "#1D4ED8", paddingLeft: "32vw"}}>
                        Выбирают нас
                    </i>
                </span>
            </h2>

            <div
                className="slider-container"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseUp}
            >
                <Slider {...settings} ref={sliderRef}>
                    <div className="slide-item">
                        <h3>Слайд 1</h3>
                        <p>Содержание слайда 1</p>
                    </div>
                    <div className="slide-item">
                        <h3>Слайд 2</h3>
                        <p>Содержание слайда 2</p>
                    </div>
                    <div className="slide-item">
                        <h3>Слайд 3</h3>
                        <p>Содержание слайда 3</p>
                    </div>
                    {/* Добавьте другие слайды как несколько <div> */}
                </Slider>
            </div>
        </div>
    );
}
