import React, { useRef, useState }  from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

export default function Advantages() {
    const settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 2,
        slidesToScroll: 1,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                    infinite: true,
                    dots: false
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                    initialSlide: 1
                }
            }
        ]
    };

    const sliderRef = useRef(null);
    const [dragStartX, setDragStartX] = useState(null);

    const handleMouseDown = (event) => {
        setDragStartX(event.clientX);
    };

    const handleMouseMove = (event) => {
        if (dragStartX !== null) {
            const slider = sliderRef.current;
            const deltaX = event.clientX - dragStartX;
            if (slider) {
                slider.scrollLeft -= deltaX;
            }
            setDragStartX(event.clientX);
        }
    };

    const handleMouseUp = () => {
        setDragStartX(null);
    };

    return (
        <div className="advantages container">
            <h2 className="advantages-title section-title flex">
                <span>
                    Почему партнеры
                </span>
                <span style={{ display: 'block' }}>
                    <i style={{color: "#1D4ED8", paddingLeft: "32vw"}}>
                        Выбирают нас
                    </i>
                </span>
            </h2>
            <div
                className="slider-container"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseUp}
            >
                <Slider {...settings}>
                    <div>
                        <h3>Мы единственные на рынке, кто предлагает решение полного цикла в связке:</h3>
                        <ul>
                            <li>фармкомпания</li>
                            <li>врач</li>
                            <li>пациент</li>
                        </ul>
                    </div>
                    <div>
                        <h3>
                            Располагаем самой полной
                            на&nbsp;рынке постоянно обновляемой базой врачей по&nbsp;целому ряду специальностей
                        </h3>
                    </div>
                    <div>
                        <h3>
                            Занимаемся рганизацией очных
                            и&nbsp;онлайн-медмероприятий любого уровня &laquo;под ключ&raquo;
                            с&nbsp;подбором экспертов и&nbsp;лекторов
                        </h3>
                    </div>
                </Slider>
            </div>
        </div>
  )
}
